import React from 'react';
import logo from './logo.svg';
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import './CoachLive.css';




function Header() {
  return (
    <Container fluid id="headerDiv">
      <span id="studentTag">Coaching:</span>
      <img src="logo.png" id="studentHeader" />
      <span id="studentUsername">Student Username </span>
    </Container>
  );
}

function Body() {
  return (
    <Row>
      <Col xs={6} id="statsSide"><Stats /></Col>
      <Col xs={6} id="mapSide">Hello</Col>
    </Row>
  )
}








function MainStats(props) {
  const tabscreen = props.tabscreen
  const mainBody = () => {
    if(tabscreen) {
      return (<div>TABSCREEN</div>)
    }
    else {
      return (<div>STAT SCREEN</div>);
    }
  }
  return (
    <div>{mainBody()}</div>
  );
}


function Stats() {
  const [tab, setTab] = React.useState(false);


  function setColor() {
    var statBtn =  document.getElementById('webButton');
    var tabBtn = document.getElementById('tabButton');
    if (tab == false) {
        statBtn.style.backgroundColor = "#D8D8D8"
        tabBtn.style.backgroundColor = "#FFFFFF"
    }
    else if(tab == true){
        statBtn.style.backgroundColor = "#FFFFFF"
        tabBtn.style.backgroundColor = "#D8D8D8"
    }
  }



  const handleClick = (evt) => {
    const target = evt.currentTarget
    if(target.id == "webButton") {
      setTab(false);
      console.log(tab)
      setColor();
    }
    else if(target.id == "tabButton") {
      setTab(true);
      console.log(tab)
      setColor();
    }
  }

  return (
    <Container id="statsContainer">
      <Row>
        <Col xs={3} id="webCol"><button id="webButton" onClick={handleClick}>Stat Website</button></Col>
        <Col xs={3} id="tabCol"><button id="tabButton" onClick={handleClick}>Tab Screen</button></Col>
      </Row>
      <Row id="statRow">
        <MainStats tabscreen={tab}/>
      </Row>
    </Container>
  );

}




export default function LiveCoachView() {
  return(
    <Container fluid="xs">
      <Row>
        <Header />
      </Row>
        <Body />
    </Container>
  );
}
