import React from 'react';
import logo from './logo.svg';
import './StartSession.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


function UserProfile() {
  return (
    <Row id="userNameBody">
      <img id="mainLogo" src="logo.png" />
      <Col>
        <h1 className="userName">Dekempsy4</h1>
        <button id="settingsButton">
          <Row >
            <img id="settingPic" src = "settings.png" />
            <h2 className="settingTag">Settings</h2>
          </Row>
        </button>
       </Col>
    </Row>
  );
}




function PastGame(props) {
  const handleClick = props.clicker;
  const game = props.game;
  const isSelected = props.game.selected

  return(
    <li className="historyItem">
      <button className="historyButton" onClick={handleClick} id={game.id}>
        <Row>
          <div className="coachTag">Coach:</div>
          <img src="logo.png" className="coachIcon" />
          <div className="testerDiv">
            <div className="coachUsername">{game.username}</div>
          </div>
        </Row>
        <Row>
          <div className="dateTag">6/19/20</div>
          <div className="durationTag">Length: 1 hour</div>
        </Row>
      </button>
    </li>
  );
}

function HistoryBar(props) {
  const handleClick = props.clicker;


  const gameList = [];

  for(let i = 0; i < props.games.length; i++) {
    gameList.push(<PastGame clicker={handleClick} game={props.games[i]} />);
  }

  return (
      <Row id="historyContainer">
        <ul id="historyList">
          {gameList}
        </ul>

        <button id="scheduleButton">
          <img src="shceduleButton.png" id="scheduleImg" width="100%" height="100%"/>
        </button>

      </Row>
  );
}

function BackButton() {
  return (
    <button id="backButton">
      <img src="backButton.png" id="backButtonImg" />
    </button>
  );
}

function ProfileSideBar(props) {
  const clickHandler = props.clicker;

  return (
      <Container>
        <UserProfile />
        <HistoryBar clicker={clickHandler} games={props.games}/>
        <BackButton />
      </Container>
  );
}

function GameDetails(props) {
  const selectedGame = props.game;
  return(
    <Row id="detailsRow">
      <h1 id="detailsHead">Game Details</h1>
      <div id="gameCoach">Coach: {selectedGame.username}</div>
      <div id="gameTime">Time: 3:45-4:45 (60 mins)</div>
    </Row>
  );
}

function Recording() {
  return (
    <Row>
      <button id="recordingButton">
        <img src="recording.png" id="recordingSymbol"/>
        <div id="recordingTag">Recording</div>
      </button>
    </Row>
  );
}

function Note() {
  return (
    <li className="note">
      <div className="noteTime">
        Time: 4:18
      </div>
      <div className="noteContent">
        Hello, here you decide to do this but you shouldve done that, this would help a lot with wave management and you wouldn't have so much trouble with this and that in the future. Dont do this too much, as it is very not beneficial, also that is very unfortunate that your teammates did this.
      </div>
    </li>
  );

}

function NotesList() {
  return (
    <Row id="notesRow">
      <div id="notesTag">Coach's Notes</div>
      <ul id="notesList">
        <Note />
        <Note />
        <Note />
        <Note />
      </ul>
    </Row>

  );
}


function DetailsBar(props) {

  const pastGames = props.games;
  let selectedGame = {
    username: 'none selected',
    date: '0/0/0',
  };

  for(let i = 0; i < pastGames.length; i++) {
    if(pastGames[i].selected == true) {
      selectedGame = pastGames[i];
    }
  }

  return (
    <Container  fluid="xs">
      <GameDetails game={selectedGame}/>
      <Recording />
      <NotesList />
    </Container>
  );

}

function StartSession() {

  const [disc, setDisc] = React.useState('');

  const handleSubmit = (evt) => {
    evt.preventDefault();
    console.log(disc);
    alert(`Submitting epic: ${disc}`);
  }

  return(
    <Row id="startSessionRow">
      <form id="startSessionSquare" onSubmit={handleSubmit}>
        <label id="liveTitle">Start Live Session</label>
        <label id="summonerInfo">Dekempsy4 (Na)</label>
        <input
          id="discInput"
          placeholder="Enter coach's discord"
          onChange={e => setDisc(e.target.value)}
        />
        <button id="startButton"><div id="startButtonTag">Start live Session!</div></button>
      </form>
    </Row>
  )
}



export default function StudentStart() {

  const [pastGames, setPastGames] = React.useState([
    {
      id: 0,
      username: "Joe Biden",
      date: "6/20/20",
      selected: true,
    },
    {
      id: 1,
      username: "Michelle Obama",
      date: "5/15/20",
      selected: false,
