import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import './CoachLive.css';


import { Stage, Layer, Rect, Text, Circle, Line, Image, Group } from 'react-konva';
import Konva from 'konva';
import NoteView from './NoteView.js'


export default function Map() {

  return (
    <Container fluid id="mapContainer">
      <NoteView />
      <Row id="mapRow">
        <Drawing />
      </Row>
    </Container>
  );
}


// CODE SNIPPET FOUND ONLINE, DON'T FULLY UNDERSTAND YET

class Drawing extends Component {
  state = {
    lines: [],
    erasing: false
  };

  handleMouseDown = () => {
      this._drawing = true;

      if(this.state.erasing === false){
        // add line
        this.setState({
          lines: [...this.state.lines, []]
        });
      }
      else if(this.state.erasing === true) {

        const stage = this.stageRef.getStage();
        const point = stage.getPointerPosition();

        console.log('ERASING pointX: ' + point.x +' point y ' + point.y)
        const tempLines = this.state.lines;
        for(let i = 0; i < tempLines.length; i++) {
          const currentLine = tempLines[i];
          console.log('currentLine ' + currentLine);

          for(let z = 0; z < currentLine.length - 1; z++) {
            const xSegment = currentLine[z];
            const ySegment = currentLine[z+1];
            console.log('checking point: ' + point.x + ' vs xSegment ' +xSegment)
            if(xSegment == point.x && ySegment == point.y) {
              alert('Deleted');
              tempLines.splice(i, 1);
              this.setState ({
                lines: tempLines
              })
            }
          }

        }
      }



  };

  handleMouseMove = e => {
    // no drawing - skipping
    //  alert('drawing ' + !this._drawing + ' erasing ' + this.state.erasing)

    if (!this._drawing) {
      return;
    }





    if(this.state.erasing === false) {
      const stage = this.stageRef.getStage();
      const point = stage.getPointerPosition();
      const { lines } = this.state;

      let lastLine = lines[lines.length - 1];
      // add point
      lastLine = lastLine.concat([point.x, point.y]);

      // replace last
      lines.splice(lines.length - 1, 1, lastLine);
      this.setState({
        lines: lines.concat()
      });
    }

  };

  handleMouseUp = () => {
    this._drawing = false;
  };

  handleClick = (evt) => {
    const currentEraserState = this.state.erasing;
    alert(!currentEraserState);
    this.setState ({
      erasing: !currentEraserState
    })
  }

  render() {
    const stageWidth = 600;
    const stageHeight = 600;

    return (
      <div>
        <button onClick={this.handleClick}>Hello</button>
        <Stage
          container={'#mapRow'}
          width={stageWidth}
          height={stageHeight}
          onContentMousedown={this.handleMouseDown}
          onContentMousemove={this.handleMouseMove}
          onContentMouseup={this.handleMouseUp}
          ref={node => {
            this.stageRef = node;
          }}
        >
          <Layer>
            <Rect
            width={stageWidth}
            height={stageHeight}
              fill={'white'}
            />

            {this.state.lines.map((line, i) => (
              <Line key={i} points={line} stroke="red" />
            ))}
          </Layer>
        </Stage>
      </div>
    );
  }
}
