import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import './CoachLive.css';


import { Stage, Layer, Rect, Text } from 'react-konva';
import Konva from 'konva';


export default function Map() {

  const [noteTab, setNoteTab] = React.useState(false);

  const handleClick = (evt) => {
    setNoteTab(!noteTab);
  }



  return (
    <Container fluid id="mapContainer">
      <Row id="noteRow">
        <NotesButton clicker={handleClick} showing={noteTab}/>
        <NoteTab show={noteTab} />
      </Row>
      <Row id="mapRow">
        <MapBody />
      </Row>
    </Container>
  );
}

function NotesButton(props) {
  const handleClick = props.clicker;
  const isShowing = props.showing;

  let buttonClass = 'noteButton';
  if(isShowing) {
    buttonClass = 'noteButton activeNoteButton';
  }

  return(
      <button className={buttonClass} onClick={handleClick}><strong>+</strong> Add Note</button>
  );
}


function NoteTab(props) {
  const isShowing = props.show;
  console.log(isShowing);


  const render = () => {
    if(isShowing === false) {
      return(<div></div>);
    }
    else {
      console.log('showing');
      return(
        <Container id="noteBox">
          <Row>
            Time
          </Row>
          <Row>
            Note Content
          </Row>
        </Container>

      );
    }
  }

  return (
    <div>
     {render()}
    </div>
  );
}

function MapBody() {
  return(
    <div id="mapBodyDiv">Epic nice</div>
  )
}
