import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import './CoachLive.css';


import { Stage, Layer, Rect, Text, Circle, Line, Image, Group, Arrow } from 'react-konva';
import Konva from 'konva';
import NoteView from './NoteView.js'
import useImage from 'use-image';


export default function Map() {
  return (
    <Container fluid id="mapContainer">
      <NoteView />
      <Row id="mapRow">
        <Drawing />
      </Row>
    </Container>
  );
}


const MapImg = () => {
  const [image] = useImage('http://ddragon.leagueoflegends.com/cdn/6.8.1/img/map/map11.png');
  return <Image image={image} width={window.innerWidth / 2.4} height={window.innerWidth / 2.4}/>;
};


const Distance = (start, end) => {
  const startsqrt = (end[0] - start[0]) * (end[0] - start[0]);
  const endsqrt = (end[1] - start[1]) * (end[1] - start[1]);
  const distance = Math.sqrt(startsqrt + endsqrt);
  return distance;
}




class Drawing extends Component {

  state = {
    lines: [],
    circlePoints: [],
    arrowPoints: [],
    currentTool: 'brush',
    eraser: false,
    brush: true,
    circle: false,
    arrow: false,
  };


  makeElement = (elementArray) => {
    const stage = this.stageRef.getStage();
    const point = stage.getPointerPosition();
    const tempArray = elementArray;
    tempArray[tempArray.length - 1][1] = [point.x, point.y];
    this.setState({
      elementArray: tempArray
    })
  }



  handleMouseDown = () => {

    this._drawing = true;

    if(this.state.brush === true){

      // add line
      this.setState({
        lines: [...this.state.lines, []]
      });
    }
    else if(this.state.eraser === true) {
      const stage = this.stageRef.getStage();
      const point = stage.getPointerPosition();
      const xRange = [point.x - 15, point.x + 15]
      const yRange = [point.y - 15, point.y + 15]

      console.log('ERASING pointX: ' + point.x +' point y ' + point.y)

      const tempLines = this.state.lines;

      for(let i = 0; i < tempLines.length; i++) {
        const currentLine = tempLines[i];

        for(let z = 0; z < currentLine.length - 1; z = z+2) {

          if(currentLine[z] > xRange[0] && currentLine[z] < xRange[1]) {
            if(currentLine[z + 1] > yRange[0] && currentLine[z + 1] < yRange[1]) {
              tempLines.splice(i, 1);

              this.setState ({
                lines: tempLines
              });
              return;
            }
          }
        }
      }
      const tempArrows = this.state.arrowPoints;
      for(let i = 0; i < tempArrows.length; i++) {
        const currentArrow = tempArrows[i];
        const startpoint = currentArrow[0]
        const endpoint = currentArrow[1]
        const lineDistance = Distance(startpoint, endpoint)
        const thisPoint = [point.x, point.y]
        const slope = (endpoint[1] - startpoint[1]) / (endpoint[0] - startpoint[0])

        const isOnLine = (aPoint) => {

          const topRange = slope * (aPoint[0] - startpoint[0]) + startpoint[1] + 10
          const bottomRange = slope * (aPoint[0] - startpoint[0]) + startpoint[1] - 10
          if( aPoint[1] < topRange && aPoint[1] > bottomRange) {
            if(Distance(startpoint, aPoint) < lineDistance && Distance(endpoint,aPoint) < lineDistance) {
              return true;
            }
          }
          return false;
        }

        console.log(isOnLine(thisPoint))

      }



    }
    else if (this.state.circle === true) {
      const stage = this.stageRef.getStage();
      const point = stage.getPointerPosition();
      this.setState({
        circlePoints: [...this.state.circlePoints, [[point.x, point.y], [point.x, point.y]]]
      });
    }
    else if (this.state.arrow === true) {
      const stage = this.stageRef.getStage();
      const point = stage.getPointerPosition();
      this.setState({
        arrowPoints: [...this.state.arrowPoints, [[point.x, point.y], [point.x, point.y]]]
      });
    }
  };

  handleMouseMove = e => {

    if (!this._drawing) {
      return;
    }

    if(this.state.brush === true) {
      const stage = this.stageRef.getStage();
      const point = stage.getPointerPosition();
      const { lines } = this.state;

      let lastLine = lines[lines.length - 1];
      // add point
      lastLine = lastLine.concat([point.x, point.y]);

      // replace last
      lines.splice(lines.length - 1, 1, lastLine);
      this.setState({
        lines: lines.concat()
      });
    }
    else if (this.state.circle === true) {
      this.makeElement(this.state.circlePoints)
    }
    else if (this.state.arrow === true) {
      this.makeElement(this.state.arrowPoints)
    }
  };

  handleMouseUp = () => {
    this._drawing = false;


  };


  eraserClick = (evt) => {
    alert('eraser on');
    this.setState ({
      eraser: true,
      currentTool: 'eraser',
      brush: false,
      circle: false,
      arrow: false,
    })
  }

  brushClick = (evt) => {
    alert('brush on');
    this.setState ({
      eraser: false,
      currentTool: 'brush',
      brush: true,
      circle: false,
      arrow: false,
    })
  }

  clearClick = (evt) => {
    alert('Cleared All');
    this.setState ({
      lines: [],
      circlePoints: [],
      arrowPoints: [],

    })
  }

  circleClick = (evt) => {
    alert('circle on');
    this.setState ({
      eraser: false,
      currentTool: 'circle',
      brush: false,
      circle: true,
      arrow: false,
    })
  }

  arrowClick = (evt) => {
    alert('arrow on');
    this.setState ({
      eraser: false,
      currentTool: 'arrow',
      brush: false,
      circle: false,
      arrow: true,
    })
  }


  RenderCircles = () => {

    let circleArray = []
    const circlePoints = this.state.circlePoints;
    for(let i = 0; i < circlePoints.length; i++) {
      const midpoint = circlePoints[i][0]
      const endpoint = circlePoints[i][1]
      console.log('midpoint: ' + midpoint + ' endpoint: ' + endpoint);

      const distance = Distance(endpoint, midpoint)
      circleArray.push(
      <Circle
        key={i}
        x={midpoint[0]}
        y={midpoint[1]}
        width={distance * 2}
        height={distance * 2}
        stroke={'blue'}

      />
    )
    }
    return circleArray;
  }


  RenderArrows = () => {
    let arrowArray = []
    const arrowPoints = this.state.arrowPoints
    arrowPoints.map((arrow, i) => {
      const startpoint = arrow[0]
      const endpoint = arrow[1]
      arrowArray.push(
        <Arrow
          key={i}
          points={[startpoint[0], startpoint[1], endpoint[0], endpoint[1]]}
          fill={'red'}
          stroke={'red'}
        />
      )

    })
    return arrowArray;
  }

  render() {
    const stageWidth = window.innerWidth / 2.4;
    const stageHeight = window.innerWidth / 2.4;

    return (
      <div id="MapDiv">
        <Container>
          <Row>
            <button
              onClick={this.brushClick}
              id="brushButton"
              className={this.state.currentTool === 'brush' ? 'activeTool' : ''}
            >
              Brush
            </button>
          </Row>
          <Row>
            <button
              onClick={this.arrowClick}
              id="arrowButton"
              className={this.state.currentTool === 'arrow' ? 'activeTool' : ''}
            >
              Arrow
            </button>
          </Row>
          <Row>
            <button
              onClick={this.eraserClick}
              id="eraserButton"
              className={this.state.currentTool === 'eraser' ? 'activeTool' : ''}
            >
              Eraser
            </button>
          </Row>
          <Row>
            <button
              onClick={this.circleClick}
              id="circleButton"
              className={this.state.currentTool === 'circle' ? 'activeTool' : ''}
            >
              Circle
            </button>
          </Row>
          <Row>
            <button
              onClick={this.clearClick}
              id="clearButton"
            >
              Clear
            </button>
          </Row>

        </Container>


        <Stage
          container={'#mapRow'}
          width={stageWidth}
          height={stageHeight}
          onContentMousedown={this.handleMouseDown}
          onContentMousemove={this.handleMouseMove}
          onContentMouseup={this.handleMouseUp}
          ref={node => {
            this.stageRef = node;
          }}
        >
          <Layer>
            <MapImg />
          </Layer>
          <Layer>
            {
              this.RenderCircles()
            }
          </Layer>
          <Layer>
            {
              this.RenderArrows()
            }
          </Layer>
          <Layer>
            {
              this.state.lines.map((line, i) => (<Line key={i} points={line} stroke="red" fill={'red'}/>))
            }
          </Layer>
        </Stage>
      </div>
    );
  }
}
