import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import './CoachLive.css';


import { Stage, Layer, Rect, Text, Circle, Line, Image, Group } from 'react-konva';
import Konva from 'konva';
import NoteView from './NoteView.js'
import useImage from 'use-image';


export default function Map() {
  return (
    <Container fluid id="mapContainer">
      <NoteView />
      <Row id="mapRow">
        <Drawing />
      </Row>
    </Container>
  );
}


const MapImg = () => {
  const [image] = useImage('http://ddragon.leagueoflegends.com/cdn/6.8.1/img/map/map11.png');
  return <Image image={image} width={600} height={600}/>;
};



class Drawing extends Component {

  state = {
    lines: [],
    circlePoints: [],
    currentTool: 'brush',
    eraser: false,
    brush: true,
    circle: false,
  };



  handleMouseDown = () => {

    if(this.state.brush === true){
      this._drawing = true;
      // add line
      this.setState({
        lines: [...this.state.lines, []]
      });
    }
    else if(this.state.eraser === true) {
      const stage = this.stageRef.getStage();
      const point = stage.getPointerPosition();
      const xRange = [point.x - 15, point.x + 15]
      const yRange = [point.y - 15, point.y + 15]

      console.log('ERASING pointX: ' + point.x +' point y ' + point.y)

      const tempLines = this.state.lines;

      for(let i = 0; i < tempLines.length; i++) {
        const currentLine = tempLines[i];

        for(let z = 0; z < currentLine.length - 1; z = z+2) {

          if(currentLine[z] > xRange[0] && currentLine[z] < xRange[1]) {
            if(currentLine[z + 1] > yRange[0] && currentLine[z + 1] < yRange[1]) {
              tempLines.splice(i, 1);

              this.setState ({
                lines: tempLines
              });
              return;
            }
          }
        }
      }
    }
    else if (this.state.circle === true) {
      const stage = this.stageRef.getStage();
      const point = stage.getPointerPosition();
      const tempCircles = this.state.circlePoints;
      this.halfCircle = [[point.x, point.y]]
    }
  };

  handleMouseMove = e => {
    // const stage = this.stageRef.getStage();
    // const point = stage.getPointerPosition();
    // console.log('x is ' + point.x + 'y is ' + point.y)

    if (!this._drawing) {
      return;
    }

    if(this.state.brush === true) {
      const stage = this.stageRef.getStage();
      const point = stage.getPointerPosition();
      const { lines } = this.state;

      let lastLine = lines[lines.length - 1];
      // add point
      lastLine = lastLine.concat([point.x, point.y]);

      // replace last
      lines.splice(lines.length - 1, 1, lastLine);
      this.setState({
        lines: lines.concat()
      });
    }
  };


  handleMouseUp = () => {
    this._drawing = false;
    if(this.state.circle === true) {
      console.log('here')
      const stage = this.stageRef.getStage();
      const point = stage.getPointerPosition();
      this.halfCircle.push([point.x, point.y])

      const tempCircles = this.state.circlePoints;
      tempCircles.push(this.halfCircle);
      console.log(tempCircles);
      this.setState({
        circlePoints: tempCircles
      })
    }
  };


  eraserClick = (evt) => {
    alert('eraser on');
    this.setState ({
      eraser: true,
      currentTool: 'eraser',
      brush: false,
      circle: false,
    })
  }

  brushClick = (evt) => {
    alert('brush on');
    this.setState ({
      eraser: false,
      currentTool: 'brush',
      brush: true,
      circle: false,
    })
  }

  clearClick = (evt) => {
    alert('Cleared All');
    this.setState ({
      lines: [],
      circlePoints: [],
    })
  }

  circleClick = (evt) => {
    alert('circle on');
    this.setState ({
      eraser: false,
      currentTool: 'circle',
      brush: false,
      circle: true,
    })
  }


  RenderCircles = () => {

    let circleArray = []
    const circlePoints = this.state.circlePoints;
    for(let i = 0; i < circlePoints.length; i++) {
      const startpoint = circlePoints[i][0]
      const endpoint = circlePoints[i][1]
        circleArray.push(
        <Circle
          key={i}
          x={Math.abs(endpoint[0] - startpoint[0])}
          y={Math.abs(endpoint[0] - startpoint[1])}
          width={100}
          height={100}
          fill={'Blue'}
        />
      )
    }
    return circleArray;
  }

  render() {
    const stageWidth = 600;
    const stageHeight = 600;

    return (
      <div>
        <Container>
          <Row>
            <button
              onClick={this.brushClick}
              id="brushButton"
              className={this.state.currentTool === 'brush' ? 'activeTool' : ''}
            >
              Brush
            </button>
          </Row>
          <Row>
            <button
              onClick={this.eraserClick}
              id="eraserButton"
              className={this.state.currentTool === 'eraser' ? 'activeTool' : ''}
            >
              Eraser
            </button>
          </Row>
          <Row>
            <button
              onClick={this.circleClick}
              id="circleButton"
              className={this.state.currentTool === 'circle' ? 'activeTool' : ''}
            >
              Circle
            </button>
          </Row>
          <Row>
            <button
              onClick={this.clearClick}
              id="clearButton"
            >
              Clear
            </button>
          </Row>

        </Container>


        <Stage
          container={'#mapRow'}
          width={stageWidth}
          height={stageHeight}
          onContentMousedown={this.handleMouseDown}
          onContentMousemove={this.handleMouseMove}
          onContentMouseup={this.handleMouseUp}
          ref={node => {
            this.stageRef = node;
          }}
        >
          <Layer>
            <MapImg />
          </Layer>
          <Layer>
            {
              this.RenderCircles()
            }
          </Layer>
          <Layer>
            {
              this.state.lines.map((line, i) => (<Line key={i} points={line} stroke="red" />))
            }
          </Layer>
        </Stage>
      </div>
    );
  }
}
