import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import './StartSession.css';
import StartSessionBar from './StartSessionBar.js';
import DetailsBar from './DetailsBar.js'


function UserProfile() {
  return (
    <Row id="userNameBody">
      <img id="mainLogo" src="logo.png" />
      <Col>
        <h1 className="userName">Dekempsy4</h1>
        <button id="settingsButton">
          <Row >
            <img id="settingPic" src = "settings.png" />
            <h2 className="settingTag">Settings</h2>
          </Row>
        </button>
       </Col>
    </Row>
  );
}




function PastGame(props) {
  const handleClick = props.clicker;
  const game = props.game;
  const isSelected = props.game.selected

  const date = () => {
    if(props.game.className === "past") {
      return (
        <Row>
          <div className="dateTag">{game.date}</div>
          <div className="durationTag">Length: 1 hour</div>
        </Row>
      )
    }
    else if (props.game.className === "proposed") {
      return (
        <Row>
            <div className="dateTag">Proposed Time: {game.date}</div>

            </Row>
          );
    }
  }
  return(
    <li className="historyItem">
      <button className={props.className} onClick={handleClick} id={game.id}>
        <Row>
          <div className="coachTag">Coach:</div>
          <img src="logo.png" className="coachIcon" />
          <div className="testerDiv">
            <div className="coachUsername">{game.username}</div>
          </div>
        </Row>
          {date()}
      </button>
    </li>
  );
}

function HistoryBar(props) {
  const handleClick = props.clicker;


  const gameList = [];

  for(let i = 0; i < props.games.length; i++) {
    if(props.games[i].className === "proposed") {
      gameList.push(<PastGame className={props.games[i].className} clicker={handleClick} game={props.games[i]} />);
    }
  }

  for(let i = 0; i < props.games.length; i++) {
    if(props.games[i].className === "past") {
      gameList.push(<PastGame className={props.games[i].className} clicker={handleClick} game={props.games[i]} />);
    }
  }

  return (
      <Row id="historyContainer">
        <ul id="historyList">
          {gameList}
        </ul>

        <button id="scheduleButton">
          <img src="shceduleButton.png" id="scheduleImg" width="100%" height="100%"/>
        </button>

      </Row>
  );
}

function BackButton() {
  return (
    <button id="backButton">
      <img src="backButton.png" id="backButtonImg" />
    </button>
  );
}

function ProfileSideBar(props) {
  const clickHandler = props.clicker;

  return (
      <Container>
        <UserProfile />
        <HistoryBar clicker={clickHandler} games={props.games}/>
        <BackButton />
      </Container>
  );
}





export default function StudentStart() {

  const [pastGames, setPastGames] = React.useState([
    {
      id: 0,
      className: "past",
      username: "Joe Biden",
      date: "6/20/20",
      selected: true,
    },
    {
      id: 1,
      className: "past",
      username: "Michelle Obama",
      date: "5/15/20",
      selected: false,
    },
    {
      id: 2,
      className: "past",
      username: "Donald Trump",
      date: "8/20/19",
      selected: false,
    },
    {
      id: 3,
      className: "past",
      username: "Joe Biden",
      date: "6/20/20",
      selected: true,
    },
    {
      id: 4,
      className: "past",
      username: "Joe Biden",
      date: "6/20/20",
      selected: true,
    },
    {
      id: 5,
      className: "past",
      username: "Joe Biden",
      date: "6/20/20",
      selected: true,
    },
    {
      id: 6,
      className: "past",
      username: "Joe Biden",
      date: "6/20/20",
      selected: true,
    },
    {
      id: 7,
      className: "past",
      username: "Joe Biden",
      date: "6/20/20",
      selected: true,
    },
    {
      id: 8,
      className: "proposed",
      username: "Micheal Joradan",
      date: "6/20/20",
      selected: true,
    }
  ]);




  const handleHistoryClick = (evt) => {
    let tempGamelist = pastGames.slice();
    const buttonId = evt.currentTarget.id;
    for(let i = 0; i < tempGamelist.length; i ++) {
      if(tempGamelist[i].selected == true) {
        tempGamelist[i].selected = false;
        if(tempGamelist[i].className === "past") {
          const element = document.getElementById(tempGamelist[i].id);
          element.style.backgroundColor = '#F0F0F0';
        }
      }
      if(tempGamelist[i].id == buttonId) {
        tempGamelist[i].selected = true;

        if(tempGamelist[i].className === "past") {
          const element = document.getElementById(tempGamelist[i].id);
          element.style.backgroundColor = "#D8D6D6";
        }
      }
    }

    setPastGames(tempGamelist);

  }




  return (
    <Row xs={12}>
      <Col sm={3} xs={12} id="sideBar"> <ProfileSideBar clicker={handleHistoryClick} games={pastGames}/> </Col>
      <Col sm={5} xs={12} id="detailsBar"> <DetailsBar games={pastGames}/> </Col>
      <Col sm={4} xs={12} id="startSessionBar" > <StartSessionBar /> </Col>
    </Row>
  );
}
