import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import './CoachLive.css';


import { Stage, Layer, Rect, Text } from 'react-konva';
import Konva from 'konva';


export default function Map() {

  const [noteTab, setNoteTab] = React.useState(false);

  const handleClick = (evt) => {
    setNoteTab(!noteTab);
  }



  return (
    <Container fluid id="mapContainer">
      <Row id="noteRow">
        <NotesButton clicker={handleClick}/>
        <NoteTab show={noteTab} />
      </Row>
      <Row id="mapRow">
        <MapBody />
      </Row>
    </Container>
  );
}

function NotesButton(props) {
  const handleClick = props.clicker;

  return(
      <button id="noteButton" onClick={handleClick}><strong>+</strong> Add Note</button>
  );
}


function NoteTab(props) {
  const isShowing = props.show;
  console.log(isShowing);


  const hidden = () => {
    return (
      <div></div>
    )
  }
  const shown = () => {
    return(
      <div>Hello</div>
    );
  }

  return (
    <div>
     {isShowing === true  ? body : hidden}
    </div>
  );
}

function MapBody() {
  return(
    <div id="mapBodyDiv">Epic nice</div>
  )
}
