// atom.config.set('core.editor.multiCursorOnClick', true);

import React from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import StartSession from './startSession.js';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";



function StudentStart() {

  const [pastGames, setPastGames] = React.useState([
    {
      id: 0,
      username: "Joe Biden",
      date: "6/20/20",
      selected: true,
    },
    {
      id: 1,
      username: "Michelle Obama",
      date: "5/15/20",
      selected: false,
    },
    {
      id: 2,
      username: "Donald Trump",
      date: "8/20/19",
      selected: false,
    }
  ]);



  const handleHistoryClick = (evt) => {
    let tempGamelist = pastGames.slice();
    const buttonId = evt.currentTarget.id;
    for(let i = 0; i < tempGamelist.length; i ++) {
      if(tempGamelist[i].selected == true) {
        tempGamelist[i].selected = false;
      }
      if(tempGamelist[i].id == buttonId) {
        tempGamelist[i].selected = true;
      }
    }

    setPastGames(tempGamelist);

  }




  return (
    <Row xs={12}>
      <Col sm={3} xs={12} id="sideBar"> <ProfileSideBar clicker={handleHistoryClick} games={pastGames}/> </Col>
      <Col sm={5} xs={12} id="detailsBar"> <DetailsBar games={pastGames}/> </Col>
      <Col sm={4} xs={12} id="startSessionBar" > <StartSession /> </Col>
    </Row>
  );
}


export default function App() {
  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/studentStart">Student Start Screen</Link>
            </li>
            <li>
              <Link to="/coachLive">Live Coach View</Link>
            </li>
          </ul>
        </nav>

        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
        <Switch>
          <Route path="/studentStart">
            <StudentStart />
          </Route>
          <Route path="/coachLive">
            <Users />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

function Home() {
  return <h2>Home</h2>;
}



function Users() {
  return <h2>Users</h2>;
}
